            function circleDrawHandler(radius) {
                while (overlays[0]) {
                    overlays.pop().setMap(null);
                }
                var circle = new google.maps.Circle({
                    center: origin,
                    clickable: true,
                    draggable: false,
                    editable: false,
                    fillColor: '#004de8',
                    fillOpacity: 0.27,
                    map: mapFullWidth,
                    radius: radius,
                    strokeColor: '#004de8',
                    strokeOpacity: 0.62,
                    strokeWeight: 1
                });
                overlays.push(circle);
                google.maps.event.addListener(circle, 'rightclick', polygonDestructionHandler);
            };

            polygonDestructionHandler = function () {
                return this.setMap(null);
            };

            function boundsUpdated(event) {
                var z = mapFullWidth.getZoom();
                var bounds = mapFullWidth.getBounds();
                var center = mapFullWidth.getCenter();
                var ne = bounds.getNorthEast();
                var sw = bounds.getSouthWest();
                var nw = new google.maps.LatLng(ne.lat(), sw.lng());
                var se = new google.maps.LatLng(sw.lat(), ne.lng());

                if (bounds && center) {
                    var b = google.maps.geometry.spherical.computeDistanceBetween(nw, ne) / 2;
                    var h = google.maps.geometry.spherical.computeDistanceBetween(center, ne);
                    var radius = ((h - b) / 400);
                }
            }

            function createMarker(placeResult, icon) {
                var iconList = ["entertainment", "schools", "food", "nightlife", "recreation", "professional", "residences", "shopping", "travel"];

                if (iconList.indexOf(icon) < 0) {
                    icon = 'general';
                }
                var markerIconUrl = '/templateresources/global/icons/map-icons/' + icon + '.png';
                var markerIcon = {
                    url: markerIconUrl,
                    scaledSize: new google.maps.Size(26, 26),
                    anchor: new google.maps.Point(13, 13),
                };

                var markerOptions = {
                    map: mapFullWidth,
                    position: placeResult.geometry.location,
                    icon: markerIcon,
                    zIndex: 1
                };

                var marker = new google.maps.Marker(markerOptions);
                google.maps.event.addListener(marker, 'spider_format', function (status) {
                    if (status == OverlappingMarkerSpiderfier.markerStatus.SPIDERFIABLE) {
                        marker.setIcon({
                            url: '/CMSScripts/Custom/RPWebParts/spiderfier/general-plus.png',
                            scaledSize: new google.maps.Size(26, 26),
                            anchor: new google.maps.Point(13, 13)
                        });
                    }
                    else {
                        marker.setIcon({
                            url: markerIconUrl,
                            scaledSize: new google.maps.Size(26, 26),
                            anchor: new google.maps.Point(13, 13),
                        });
                    }
                });
                oms.addMarker(marker);
                markersArray.push(marker);

                google.maps.event.addListener(marker, 'spider_click', function () {
                    clicked = true;

                    getPlaceDetails(placeResult, marker);
                    this.setOptions({ zIndex: 10 });
                });

                google.maps.event.addListener(marker, 'closeclick', function () {
                    clicked = false;
                });
            }

            function isValid(input1, input2) {
                if (input1 != undefined || input2 != undefined) {
                    return true;
                }
                else {
                    return false;
                }
            }

            function getPlaceDetails(placeResult, marker){
                if (infoWindow != undefined) {
                    infoWindow.close();
                }

                var request = {
                    reference: placeResult.reference
                };

                mapService.getDetails(request, function(details, status) {
                    var priceRating = '';
                    if (details.price_level != undefined) {
                        for (i = 1; i <= details.price_level; i++) {
                            priceRating += '$';
                        }
                    }

                    var photoUrl = '';
                    if (details.photos != undefined) {
                        if (details.photos.length > 0) {
                            photoUrl = details.photos[0].getUrl({ 'maxWidth': 300, 'maxHeight': 300 });
                        }
                    }

                    var addr = details.address_components;
                    var content = '
';
                    content += (photoUrl != '') ? '
' : '';
                    content += '
' + details.name + '';
                    content += (details.rating != undefined) ? '' + details.rating + '' : '';
                    content += '
';
                    content += (getAddressComponent(addr, 'street_number') != undefined) ? getAddressComponent(addr, 'street_number') + ' ' : '';
                    content += (getAddressComponent(addr, 'route', 'short') != undefined) ? getAddressComponent(addr, 'route', 'short') : '';
                    content += (isValid(getAddressComponent(addr, 'street_number'), getAddressComponent(addr, 'route', 'short'))) ? '
' : '';
                    content += (getAddressComponent(addr, 'locality') != undefined) ? getAddressComponent(addr, 'locality') + ', ' : '';
                    content += (getAddressComponent(addr, 'administrative_area_level_1', 'short') != undefined) ? getAddressComponent(addr, 'administrative_area_level_1', 'short') + ' ' : '';
                    content += (getAddressComponent(addr, 'postal_code', 'short') != undefined) ? getAddressComponent(addr, 'postal_code', 'short') : '';
                    content += '';
                    content += (details.formatted_phone_number != undefined) ? '
' + details.formatted_phone_number + '' : '';
                    content += '
Get Directions' + ((priceRating != '') ? ' • ' + priceRating + '' : '') + '';
                    content += '';

                    infoWindow = new google.maps.InfoWindow({ content: content });

                    infoWindow.open(mapFullWidth, marker);
                });
            }

            function getAddressComponent(components, type, length){
                var str;
                $(components).each(function(){
                    if (this.types[0] == type){
                        str = (length == 'short') ? this.short_name : this.long_name;
                        return;
                    }
                });
                return str;
            }

            function findByKeyword(keyword, icon) {
                currentKeyword = keyword;
                currentIcon = icon;

                var excludedKeywords = $.parseJSON('[]');
                var excludedBusinesses = $.parseJSON('[]');
                var filteredWords = "sex,strip club,adult entertainment,strip bar,XXX,porn,escort,adult video,gentlemen's club,adult bookstore,adult club";
                if (excludedKeywords.length > 0) {
                    filteredWords += ',' + excludedKeywords.join(',');
                }
                if (excludedBusinesses.length > 0) {
                    filteredWords += ',' + excludedBusinesses.join(',');
                }
                var fwordsArray = filteredWords.split(",");
                var request = {
                    keyword: keyword,
                    location: origin,
                    radius: currentRadius
                };

                $('.neighborhood-widget__loader').show();

                mapService.nearbySearch(request, function (results, status) {
                    deleteOverlays();
                    var customPinExists = false;
                    $(customMapPinsObj).each(function (i, v) {
                        $(v.Category).each(function (j, w) {
                            if (w == icon || (!icon && keyword && w == keyword.trim())) {
                                customPinExists = true;

                                var markerIconUrl = '/templateresources/global/icons/map-icons/' + (icon ? icon : 'general') + '.png';
                                var markerIcon = {
                                    url: markerIconUrl,
                                    scaledSize: new google.maps.Size(26, 26),
                                    anchor: new google.maps.Point(13, 13)
                                };

                                var customMarkerOptions = {
                                    position: new google.maps.LatLng(v.Latitude, v.Longitude),
                                    map: mapFullWidth,
                                    icon: markerIcon,
                                    geometry: {
                                        location: new google.maps.LatLng(v.Latitude, v.Longitude)
                                    },
                                    name: v.Name,
                                    vicinity: v.Address,
                                    addressState: v.State,
                                    addressPostalCode: v.Zip,
                                    website: v.Url,
                                    city: v.City
                                };
                                var customMarker = new google.maps.Marker(customMarkerOptions);
                                google.maps.event.addListener(customMarker, 'spider_format', function (status) {
                                    if (status == OverlappingMarkerSpiderfier.markerStatus.SPIDERFIABLE) {
                                        customMarker.setIcon({
                                            url: '/CMSScripts/Custom/RPWebParts/spiderfier/general-plus.png',
                                            scaledSize: new google.maps.Size(26, 26),
                                            anchor: new google.maps.Point(13, 13)
                                        });
                                    }
                                    else {
                                        customMarker.setIcon({
                                            url: markerIconUrl,
                                            scaledSize: new google.maps.Size(26, 26),
                                            anchor: new google.maps.Point(13, 13),
                                        });
                                    }
                                });

                                oms.addMarker(customMarker);
                                markersArray.push(customMarker);

                                var customPin = '
' +
                                    '
' + v.Name + '' +
                                    '
' + v.Address + '
' + v.City + ', ' + v.State + ' ' + v.Zip + '' +
                                    '
Get Directions';

                                google.maps.event.addListener(customMarker, 'spider_click', function () {
                                    if (infoWindow != undefined) {
                                        infoWindow.close();
                                    }
                                    infoWindow = new google.maps.InfoWindow({ content: customPin });
                                    infoWindow.open(mapFullWidth, customMarker);

                                    $('.neighborhood-widget__marker').parent().parent().parent().prev().find('> div').eq(7).css('border-top', '3px solid #a0c7cb;');
                                });
                            }
                        });
                    });

                    if (status == google.maps.places.PlacesServiceStatus.OK) {
                        $(results).each(function () {
                            var temp = 0;
                            for (var i = 0; i < fwordsArray.length; i++) {
                                var re = new RegExp('(^|\\s+)' + fwordsArray[i] + '(\\s+|$)', 'gi');
                                if (this.name.match(re) != null) {
                                    temp += this.name.match(re);
                                }
                            }
                            if (temp == 0 && ('false' == 'false')) {
                                createMarker(this, currentIcon);
                            }
                        });

                        if (markersArray.length > 0) {
                            var bounds = new google.maps.LatLngBounds();

                            for (var i = 0; i < markersArray.length; i++) {
                                bounds.extend(markersArray[i].getPosition());
                            }
                                              
                            bounds.extend(origin);

                            mapFullWidth.fitBounds(bounds);
                        }                                                      
                    }

                    if ((status == "ZERO_RESULTS" && !customPinExists) || (markersArray.length == 0)) {
                        $('.search-no-results').addClass('show-message');
                    }
                    else { $('.search-no-results').removeClass('show-message'); }
                    $('.neighborhood-widget__loader').hide('slow');
                });
            }

            function deleteOverlays() {
                if (markersArray) {
                    while (markersArray[0]) {
                        markersArray.pop().setMap(null);
                    }
                }
            }

