                    $(this).addClass('active');
                    $(this).siblings().removeClass('active');

                    findByKeyword($(this).text(), $(this).data('icon'));
                    $('.neighborhood-widget__search input').val($(this).text());

                    if ($(window).width() < 768) {
                        hideCategories();
                    }
                });

                $('.neighborhood-widget__handle').click(function () {
                    if (!categoriesHidden) {
                        hideCategories();
                    } else {
                        showCategories();
                    }
                });

