                function getSubmitTimeStamp() {
                    let date = new Date();
                    var formattedDate = String(date.getUTCFullYear() + "-" + date.getUTCDate() + "-" + date.getUTCDay() + " " + date.getUTCHours() + ":" + date.getUTCMinutes() + ":" + date.getUTCSeconds());
                    console.log("Get UTC date time/stamp: " + formattedDate)
                    return formattedDate;
                }

                function sendContactInfo() {
                    if (enableRecaptcha == "true") {
                        o['cmsSiteId'] = cmsSiteId;
                        o['pageName'] = pageName;
						o['widgetName'] = widgetName;
                    }

					o['googleAnalyticsId'] = googleAnalyticsId;
					o['ipaddress'] = ipAddress;
                    o['leadtime'] = getSubmitTimeStamp();

                    $("#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form input").each(function () {
                        var postData = $(this).data('post-data');
                        o[$(this).attr('name')] = (postData == null || postData == undefined) ? $(this).val() : postData;                 
                    });

                    $("#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form select").each(function () {
                        if ($(this).attr('name') == 'usersource')
                            o[$(this).attr('name')] = $('option:selected', this).text();
                        else
                            o[$(this).attr('name')] = $(this).val();
                    });

                    $("#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form textarea").each(function () {
                        o[$(this).attr('name')] = escape($(this).val());
                    });

                    if ($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #e-mail').val().length == 0) {
                        var id = $("#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #bedbathid option:selected").val();

                        try {

                            //format number before Hash
                            var formatPhone = rptracker.formatPhoneNumber($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #dayPhone1').val(), countryCode);
                            //Hash all the parts before sending them to RPTracker.
                            var first_name = rptracker.hash($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #firstname').val());
                            var last_name = rptracker.hash($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #lastname').val());
                            var phone = rptracker.hash(formatPhone);
                            var email = rptracker.hash($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #email').val());
                            var source = $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #usersource option:selected').text();

                            var bed = 0;
                            var bath = 0;
                            for (var key in kpv) {
                                // skip loop if the property is from prototype
                                if (!kpv.hasOwnProperty(key)) continue;

                                var item = kpv[key];
                                if (item.key === parseInt(id)) {
                                    bed = item.bed;
                                    bath = item.bath;
                                    break;
                                }
                            }

                            //Send the informaiton back over to RPTracker.
                            rptracker.track('Form Submitted', {
                                first_name: first_name,
                                last_name: last_name,
                                email: email,
                                phone_number: phone,
                                bed: bed,
                                bath: bath,
                                movein_date: $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #movedate').val(),
                                source: source,
                                comments: escape(($('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #msg').val() === undefined || $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #msg').val().length <= 0 ? "" : $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .contact-mobile-form #msg').val()))
                            });
                        }
                        catch (err) {
                            console.log("RPTracker failed but it is not a dependancy for the contact form. Please continue.");
                        }

                        $.ajax({
                            type: "POST",
                            dataType: "json",
                            cache: false,
                            timeout: timeoutValue,
                            url: "CMSSiteManager/Callback.aspx?act=set_contact",
                            data: o,
                            success: function (json) {
                                if (json.ErrorNumber) {
                                    formSuccess = false;
                                    setFormErrorMessage("Failed to submit guest card.");
                                    $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('value', 'Submit');
                                    $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('disabled', false);
                                } else {
                                    try {
                                        $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-pnlMobile').hide();
                                        let customDimensions = {}
                                        customDimensions.dimension15 = json.LeaseStarLeadId;
                                        if (json.Lead2LeaseLeadId != 0)
                                            customDimensions.dimension17 = json.Lead2LeaseLeadId;
                                        f_analyticsSendEvent('Contact', 'Information Request', 'Thank you', customDimensions);
                                    } catch (e) {
									}
                                    window.open('/Thank-You.aspx', '_self');
                                }
                            },
                            beforeSend: function (json) {
                                formSuccess = true;
                                $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('value', 'Sending...');
                                $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('disabled', true);
                                setFormErrorMessage("");
                            },
                            error: function (e) {
                                formSuccess = false;
                                $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('value', 'Submit');
                                $('#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf .cf-submit').prop('disabled', false);
                                setFormErrorMessage("There was an error submitting your information.");
                            }
                        });
                    } 
                }

				$("#p_lt_zonePagePlaceholder_pageplaceholder_p_lt_zoneContact_ContactSectionText_ContactSectionText_zone_ContactMainForm_rpcf  .contact-mobile-form #btnSubmit").click(function () {

