body.script:
        $.getScript("https://telescope.realpage.com/tracker?systemid=PLSW6")
            .done(function () {
                var x = 0;

                var setIntervalID = setInterval(function () {
                    if (rptracker.getTrackingId()) {
                        clearInterval(setIntervalID);

                        var uid = rptracker.getTrackingId();
                        localStorage.setItem('rptracker', uid);
                    }

                    x++;

                    if (x > 10) {
                        clearInterval(setIntervalID);
                        console.log("Failed to find telescope id, aborting.");
                    }
                }, 300);
            });
